var five = require('johnny-five');
var board = new five.Board();

board.on('ready', function(){
  var servo = new five.Servo(6);
  var button = new five.Button(4);

  button.on('down', function(){
    servo.sweep();
  });
  
  button.on('hold', function(){
    servo.stop();
  });
  
  // this.repl.inject({
  //   servo: servo,
  // });
});