var five = require('johnny-five');
var exec = require('child_process').exec;
var board = new five.Board();

board.on('ready', function(){
  var clone = new five.Button(4);
  var push = new five.Button(8);
  
  clone.on('up', function(){
    execute("git clone git@github.com:Tille/barcamp.git");    
  });

  push.on('up', function(){
    execute("git add .");
    execute("git commit -am 'fdsafda'");
  });
});

function execute(command, callback){
  exec(command, function(error, stdout, stderr){ 
    console.log(stdout); 
  });
}