var five = require('johnny-five');

var board = new five.Board();
var leds = [];

function callback(){
  leds[0] = new five.Led(9)
  leds[1] = new five.Led(7);
  leds[2] = new five.Led(12);
  var ind = 0;

  this.repl.inject({
    board: board,
    led: leds[0],
    led2: leds[1],
    led3: leds[2]
  });

  // this.loop(1000, function(){
  //   animate(ind);
  //   ind = (ind+1)%3;
  // });
}

function animate(ind){  
  for( var i = 0, len = leds.length; i < len; ++i )
    leds[i].off();
  leds[ind].on();
}

board.on('ready', callback );