var five = require("johnny-five");

five.Board().on('ready', function(){
  var sensor = new five.Sensor({
    pin: "A0",
    freq: 500
  });
  
  sensor.on('data', function(){
    var voltage = this.value * 0.48828125;
    var celsius = voltage;
    var fahrenhelt = celsius * (9/5) * 32;
    
    console.log(celsius, "ºC", fahrenhelt, "ºF");
  });
});